import { css } from 'styled-components/macro';

const fonts = css`
  @font-face {
    font-family: 'Metal Lord';
    src: url('../fonts/Metal Lord V1.woff2') format('woff2'),
    url('../fonts/Metal Lord V1.woff') format('woff');
    font-weight: 400;
    font-style: normal;
  }

  @font-face {
    font-family: 'Metal Lord';
    src: url('../fonts/Metal Lord V1.woff2') format('woff2'),
    url('../fonts/Metal Lord V1.woff') format('woff');
    font-weight: 700;
    font-style: normal;
  }

  @font-face {
    font-family: 'Metal Lord';
    src: url('../fonts/Metal Lord V1.woff2') format('woff2'),
    url('../fonts/Metal Lord V1.woff') format('woff');
    font-weight: 900;
    font-style: normal;
  }
`;

export default fonts;
